use rust_xlsxwriter::{Workbook, Worksheet, XlsxError};

mod common;

// Test to demonstrate setting the first visible tab in a group of worksheets.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    let worksheet1 = Worksheet::new();
    let worksheet2 = Worksheet::new();
    let worksheet3 = Worksheet::new();
    let worksheet4 = Worksheet::new();
    let worksheet5 = Worksheet::new();
    let worksheet6 = Worksheet::new();
    let worksheet7 = Worksheet::new();
    let mut worksheet8 = Worksheet::new();
    let worksheet9 = Worksheet::new();
    let worksheet10 = Worksheet::new();
    let worksheet11 = Worksheet::new();
    let worksheet12 = Worksheet::new();
    let worksheet13 = Worksheet::new();
    let worksheet14 = Worksheet::new();
    let worksheet15 = Worksheet::new();
    let worksheet16 = Worksheet::new();
    let worksheet17 = Worksheet::new();
    let worksheet18 = Worksheet::new();
    let worksheet19 = Worksheet::new();
    let mut worksheet20 = Worksheet::new();

    worksheet8.set_first_tab(true);
    worksheet20.set_active(true);

    workbook.push_worksheet(worksheet1);
    workbook.push_worksheet(worksheet2);
    workbook.push_worksheet(worksheet3);
    workbook.push_worksheet(worksheet4);
    workbook.push_worksheet(worksheet5);
    workbook.push_worksheet(worksheet6);
    workbook.push_worksheet(worksheet7);
    workbook.push_worksheet(worksheet8);
    workbook.push_worksheet(worksheet9);
    workbook.push_worksheet(worksheet10);
    workbook.push_worksheet(worksheet11);
    workbook.push_worksheet(worksheet12);
    workbook.push_worksheet(worksheet13);
    workbook.push_worksheet(worksheet14);
    workbook.push_worksheet(worksheet15);
    workbook.push_worksheet(worksheet16);
    workbook.push_worksheet(worksheet17);
    workbook.push_worksheet(worksheet18);
    workbook.push_worksheet(worksheet19);
    workbook.push_worksheet(worksheet20);

    workbook.save(filename)?;

    Ok(())
}

#[test]
fn test_firstsheet01() {
    let test_runner = common::TestRunner::new()
        .set_name("firstsheet01")
        .set_function(create_new_xlsx_file)
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}
