use rust_xlsxwriter::{Format, Workbook, XlsxError};

mod common;

// Test to demonstrate simple hyperlinks.
fn create_new_xlsx_file_1(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    let worksheet = workbook.add_worksheet();
    let format = Format::new().set_hyperlink();

    worksheet.write_url_with_options(0, 0, "http://www.perl.org/", "", "", Some(&format))?;

    workbook.save(filename)?;

    Ok(())
}

// Test to demonstrate simple hyperlinks, with implicit format.
fn create_new_xlsx_file_2(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    let worksheet = workbook.add_worksheet();

    worksheet.write_url_with_options(0, 0, "http://www.perl.org/", "", "", None)?;

    workbook.save(filename)?;

    Ok(())
}

// Test to demonstrate simple hyperlinks, with implicit format and write_url().
fn create_new_xlsx_file_3(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    let worksheet = workbook.add_worksheet();

    worksheet.write_url(0, 0, "http://www.perl.org/")?;

    workbook.save(filename)?;

    Ok(())
}

#[test]
fn test_hyperlink28_1() {
    let test_runner = common::TestRunner::new()
        .set_name("hyperlink28")
        .set_function(create_new_xlsx_file_1)
        .unique("1")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}

#[test]
fn test_hyperlink28_2() {
    let test_runner = common::TestRunner::new()
        .set_name("hyperlink28")
        .set_function(create_new_xlsx_file_2)
        .unique("2")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}

#[test]
fn test_hyperlink28_3() {
    let test_runner = common::TestRunner::new()
        .set_name("hyperlink28")
        .set_function(create_new_xlsx_file_3)
        .unique("3")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}
