use crate::common;
use rust_xlsxwriter::{Chart, ChartType, HeaderImagePosition, Image, Workbook, XlsxError};

// Create a rust_xlsxwriter file to compare against an Excel file.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    let worksheet = workbook.add_worksheet();

    // Add some test data for the chart(s).
    let data = [[1, 2, 3, 4, 5], [2, 4, 6, 8, 10], [3, 6, 9, 12, 15]];
    for (col_num, col_data) in data.iter().enumerate() {
        for (row_num, row_data) in col_data.iter().enumerate() {
            worksheet.write(row_num as u32, col_num as u16, *row_data)?;
        }
    }

    // Cerate a chart.
    let mut chart = Chart::new(ChartType::Bar);
    chart.set_axis_ids(61357056, 61363328);

    chart.add_series().set_values(("Sheet1", 0, 0, 4, 0));
    chart.add_series().set_values(("Sheet1", 0, 1, 4, 1));
    chart.add_series().set_values(("Sheet1", 0, 2, 4, 2));

    let image = Image::new("tests/input/images/watermark.png")?;

    // Create a chartsheet and add the chart.
    let chartsheet = workbook.add_chartsheet();
    chartsheet.set_active(true);
    chartsheet.set_header("&C&G");
    chartsheet.set_header_image(&image, HeaderImagePosition::Center)?;

    chartsheet.insert_chart(8, 4, &chart)?;

    workbook.save(filename)?;

    Ok(())
}

#[test]
fn test_chartsheet10() {
    let test_runner = common::TestRunner::new()
        .set_name("chartsheet10")
        .set_function(create_new_xlsx_file)
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}
