use crate::common;
use rust_xlsxwriter::{Format, Image, Workbook, XlsxError};

// Test to demonstrate adding images to worksheets.
fn create_new_xlsx_file_1(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    // Set the default format for the workbook.
    let format = Format::new().set_font_name("Arial").set_font_size(12);
    workbook.set_default_format(&format, 20, 80)?;

    let worksheet = workbook.add_worksheet();

    // Set user column width and row height to test positioning calculation.
    worksheet.set_column_width_pixels(4, 96)?;
    worksheet.set_row_height_pixels(8, 32)?;

    // Check that objects are positioned correctly with the default format.
    let image = Image::new("tests/input/images/red.png")?.set_alt_text("red.png");
    worksheet.insert_image_with_offset(8, 4, &image, 32, 0)?;

    // Set column to text column width less than 1 character.
    worksheet.set_column_width_pixels(6, 10)?;

    workbook.save(filename)?;

    Ok(())
}

// Use character sizing for column and row.
fn create_new_xlsx_file_2(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    // Set the default format for the workbook.
    let format = Format::new().set_font_name("Arial").set_font_size(12);
    workbook.set_default_format(&format, 20, 80)?;

    let worksheet = workbook.add_worksheet();

    // Set user column width and row height to test positioning calculation.
    worksheet.set_column_width(4, 9.89)?;
    worksheet.set_row_height(8, 24.0)?;

    // Check that objects are positioned correctly with the default format.
    let image = Image::new("tests/input/images/red.png")?.set_alt_text("red.png");
    worksheet.insert_image_with_offset(8, 4, &image, 32, 0)?;

    // Set column to text column width less than 1 character.
    worksheet.set_column_width(6, 0.63)?;

    workbook.save(filename)?;

    Ok(())
}

// Use insert_image_fit_to_cell_centered() method.
fn create_new_xlsx_file_3(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    // Set the default format for the workbook.
    let format = Format::new().set_font_name("Arial").set_font_size(12);
    workbook.set_default_format(&format, 20, 80)?;

    let worksheet = workbook.add_worksheet();

    // Set user column width and row height to test positioning calculation.
    worksheet.set_column_width(4, 9.89)?;
    worksheet.set_row_height(8, 24.0)?;

    // Check that objects are positioned correctly with the default format.
    let image = Image::new("tests/input/images/red.png")?.set_alt_text("red.png");
    worksheet.insert_image_fit_to_cell_centered(8, 4, &image)?;

    // Set column to text column width less than 1 character.
    worksheet.set_column_width(6, 0.63)?;

    workbook.save(filename)?;

    Ok(())
}

#[test]
fn test_default_format10_1() {
    let test_runner = common::TestRunner::new()
        .set_name("default_format10")
        .set_function(create_new_xlsx_file_1)
        .unique("1")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}

#[test]
fn test_default_format10_2() {
    let test_runner = common::TestRunner::new()
        .set_name("default_format10")
        .set_function(create_new_xlsx_file_2)
        .unique("2")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}

#[test]
fn test_default_format10_3() {
    let test_runner = common::TestRunner::new()
        .set_name("default_format10")
        .set_function(create_new_xlsx_file_3)
        .unique("3")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}
